mapper.select:
select rownum rn,t.* from ( select distinct pqh.qpnumber QPNumber,pqh.qpenddate QPEndDate, (case when t.bjzt like '%取消%' then '取消' when t.bjzt like '%终止%' then '终止' when t.bjzt like '%进行中%' then '进行中' when t.bjzt like '%已过期%' then '已过期' else '结束' end) QPStatus, pqh.qporiginaldate QPOriginalDate,pqh.qpcompanycode QPCompanyCode,pqh.qpcompanyname QPCompanyName, pqh.qpbranchcode QPBranchCode,(select trim(drdl01) from prodctl.f0005 where drsy = '00' and drrt = '33' and trim(drky) = pqh.qpbranchcode) QPBranchName,pqh.qpcreateby QPCreateBy,pqh.qporiginator QPOriginator, pqh.qpcompanycode||' '||pqh.qpcompanyname QPCompany, pqhs.qpslsuppliercode,trim(f0101.abalph) provider_name,tp.quotedprice_total,tiq.qpdslisquoted, trim((case when tiq.qpdslisquoted = '0' then '*' end) || ' ' || pqhs.qpslsuppliercode) quoted_tag from PA_QUOTEDPRICE pqh join PA_QUOTEDPRICE_SUPPLIER pqhs on pqh.qpnumber = pqhs.qpnumber join (select pqh.qpnumber,pqhs.qpslsuppliercode, listagg((case when pqh.QPStatus = '3' then '终止' when pqh.QPStatus = '4' then '取消' when pqds.qpnumber is null and to_char(sysdate,'YYYY-MM-DD HH24:MI') > pqh.qpenddate then '已过期' when pqds.qpnumber is not null then '结束' when pqds.qpnumber is null and to_char(sysdate,'YYYY-MM-DD HH24:MI') <= pqh.qpenddate then '进行中' end), ',') within GROUP(order by(pqh.qpnumber)) as bjzt from PA_QUOTEDPRICE pqh join PA_QUOTEDPRICE_SUPPLIER pqhs on pqh.qpnumber = pqhs.qpnumber left join PA_QUOTEDPRICE_DETAIL_SUPPLIER pqds on pqhs.qpnumber = pqds.qpnumber and pqhs.qpslsuppliercode = pqds.qpdslsuppliercode group by pqh.qpnumber,pqhs.qpslsuppliercode ) t on pqhs.qpnumber = t.qpnumber and pqhs.qpslsuppliercode = t.qpslsuppliercode left join ( select pqds.qpnumber,pqds.qpdslsuppliercode, sum(case when pqds.qpdsluntaxmoney is null or pqds.qpdsluntaxmoney = -1 then 0 else pqds.qpdsluntaxmoney end) quotedprice_total from PA_QUOTEDPRICE_DETAIL_SUPPLIER pqds group by pqds.qpnumber,pqds.qpdslsuppliercode ) tp on pqhs.qpnumber = tp.qpnumber and pqhs.qpslsuppliercode = tp.qpdslsuppliercode left join (select distinct pqds.qpnumber,pqds.qpdslsuppliercode,pqds.qpdslisquoted from PA_QUOTEDPRICE_DETAIL_SUPPLIER pqds where pqds.qpdslisquoted = '0') tiq on pqhs.qpnumber = tiq.qpnumber and pqhs.qpslsuppliercode = tiq.qpdslsuppliercode left join f0101 on to_char(f0101.aban8) = pqhs.qpslsuppliercode
pqh.qpbranchcode in (${branch_code})
and pqh.qpmaterialtype in (${materiel_type})
and pqh.qpurgent in (${urgency})
) t
and ${head_conditions}
order by ${sort_colum} ${sort_rule}

