rcode = tp.qpdslsuppliercode left join (select distinct pqds.qpnumber,pqds.qpdslsuppliercode,pqds.qpdslisquoted from PA_QUOTEDPRICE_DETAIL_SUPPLIER pqds where pqds.qpdslisquoted = '0') tiq on pqhs.qpnumber = tiq.qpnumber and pqhs.qpslsuppliercode = tiq.qpdslsuppliercode left join f0101 on to_char(f0101.aban8) = pqhs.qpslsuppliercode
pqh.qpbranchcode in (${branch_code})
and pqh.qpmaterialtype in (${materiel_type})
and pqh.qpurgent in (${urgency})
) t
and ${head_conditions}
order by ${sort_colum} ${sort_rule}

select rownum rn,t.* from ( select distinct pqhs.qpslsuppliercode QPSLSupplierCode,pqd.qpnumber QPNumber,pqd.qpdfindnum QPDFindNum, pqd.qpditemno QPDItemNo,pqd.qpditemname QPDItemName, (case when NVL(pqd.qpdstatus,'N') = '3' then '终止' when NVL(pqd.qpdstatus,'N') = '4' then '取消' when pqds.qpnumber is not null then '结束' when NVL(pqd.qpdstatus,'N') = 'N' and pqds.qpnumber is null and to_char(sysdate,'YYYY-MM-DD HH24:MI') > pqh.qpenddate then '已过期' end) QPDSLStatus, pqd.qpdmanufactureno QPDManufactureNo,pqd.qpdmanufacturename QPDManufactureName,pqd.qpdspec QPDSpec,pqd.qpditemunit QPDItemUnit, pqd.qpddrawno QPDDrawNo,pqd.qpdrev QPDRev,pqd.qpdcustomcode QPDCustomCode,pqd.qpdcustomname QPDCustomName, pqd.qpdstandardcode QPDStandardCode,pqd.qpdstandardname QPDStandardName,pqd.qpdmodule QPDModule, pqd.qpdisurgent QPDIsUrgent,pqd.qpdrequireddate QPDRequiredDate,pqh.qpcreatedate QPCreateDate, pqds.qpdslsupplieruseraccount QPDSLSupplierUserAccount,pqds.qpdslsupplierusername QPDSLSupplierUserName, pqh.qpsendaddresscode QPSendAddressCode,pqh.qpsendaddress QPSendAddress,pqh.qpcurrency, pqd.qpdrequirebillno QPDRequireBillNo,'OR' ysdhlx,pqd.qpdcompanycode QPDCompanyCode, pqd.qpdcompanyname QPDCompanyName,pqd.qpdrbnfindnum QPDRBNFindNum,pqh.qpenddate QPEndDate, pqds.qpdslcreatedate QPDAnswerDate,pqh.qporiginaldate QPOriginalDate, pqh.qporiginator QPOriginator,pqh.qpcompanycode QPCompanyCode,pqh.qpcompanyname QPCompanyName, pqd.qpdquitdate QPDQuitDate,pqd.qpdstopdate QPDStopDate, pqd.qpddepartmentcode QPDDepartmentCode,pqd.qpddepartmentname QPDDepartmentName, pqd.qpdrbnremark QPDRBNRemark,pqds.qpdslacceptdate QPDSLAcceptDate, pqds.qpdsluntaxmoney QPDSLUnTaxMoney,pqds.qpdslremark QPDSLRemark, trim(abalph) provider_name,cusOA.email provider_email, pqhs.qpslsuppliercode||' '||trim(abalph) supplier, pqd.qpdcustomcode||' '||pqd.qpdcustomname QPDCustom, pqd.qpdstandardcode||' '||pqd.qpdstandardname QPDStandard, pqh.qpsendaddresscode||' '||pqh.qpsendaddress QPSend, pqd.qpdcompanycode||' '||pqd.qpdcompanyname QPDCompany, pqh.qpcompanycode||' '||pqh.qpcompanyname QPCompany from PA_QUOTEDPRICE_DETAIL pqd join PA_QUOTEDPRICE pqh on pqd.qpnumber = pqh.qpnumber join PA_QUOTEDPRICE_SUPPLIER pqhs on pqh.qpnumber = pqhs.qpnumber left join PA_QUOTEDPRICE_DETAIL_SUPPLIER pqds on pqd.qpnumber = pqds.qpnumber and pqd.qpdfindnum = pqds.qpdslfindnum and pqhs.qpslsuppliercode = pqds.qpdslsuppliercode left join f0101 on to_char(f0101.aban8) = pqhs.qpslsuppliercode left join (select * from CRM_CustomerInfo where type = 2) cusOA on pqhs.qpslsuppliercode = cusOA.engname
pqh.qpbranchcode in (${branch_code})
and pqh.qpmaterialtype in (${materiel_type})
and pqh.qpurgent in (${urgency})
) t
and ${detail_conditions}
order by ${sort_colum} ${sort_rule}
